// Initialize total counts
let totalCounts = {
    high: 0,
    medium: 0,
    low: 0
};

function countPriorities(jsonText) {
    // Ensure we're working with a string
    const text = typeof jsonText === 'string' ? jsonText : JSON.stringify(jsonText);

    // Define the priority patterns
    const patterns = {
        high: /Priority:\s*High/gi,
        medium: /Priority:\s*Medium/gi,
        low: /Priority:\s*Low/gi
    };

    // Count occurrences
    const counts = {
        high: (text.match(patterns.high) || []).length,
        medium: (text.match(patterns.medium) || []).length,
        low: (text.match(patterns.low) || []).length
    };

    // Add to total counts
    totalCounts.high += counts.high;
    totalCounts.medium += counts.medium;
    totalCounts.low += counts.low;

    // Create result object for individual input
    return {
        individualCounts: counts,
        individualSummary: `Priority Counts for this input:
- High Priority: ${counts.high} occurrences
- Medium Priority: ${counts.medium} occurrences
- Low Priority: ${counts.low} occurrences`,
        individualTotal: counts.high + counts.medium + counts.low
    };
}

// Process all items
const results = items.map(item => countPriorities(item.json));

// Add total summary to the last item
const totalSummary = {
    totalCounts: totalCounts,
    totalSummary: `Total Priority Counts Across All Inputs:
- High Priority: ${totalCounts.high} occurrences
- Medium Priority: ${totalCounts.medium} occurrences
- Low Priority: ${totalCounts.low} occurrences`,
    grandTotal: totalCounts.high + totalCounts.medium + totalCounts.low
};

// Return array of results with total counts in each item
return results.map(result => ({
    ...result,
    totalCounts: totalCounts,
    totalSummary: totalSummary.totalSummary,
    grandTotal: totalSummary.grandTotal
}));

